    ************************************************************************/
    --TYPE RECORD IS REFCURSOR;
  CUR_DSequence RECORD;
  v_NextNo VARCHAR(32); --OBTG:VARCHAR2--
    
BEGIN
    
    IF AD_isTriggerEnabled()='N' THEN IF TG_OP = 'DELETE' THEN RETURN OLD; ELSE RETURN NEW; END IF; 
