      AND AD_Table_ID IN
      (
      SELECT AD_Table_ID
      FROM AD_Column
      WHERE ColumnName='DocumentNo'
        OR ColumnName='Value'
      )
      AND NOT EXISTS
      (SELECT 1 FROM AD_Sequence s  WHERE s.Name = 'DocumentNo_' || t.TableName
