  v_Processed VARCHAR(60) ;
  v_C_INVOICE_ID VARCHAR(32) ; --OBTG:VARCHAR2--
    
BEGIN
    
    IF AD_isTriggerEnabled()='N' THEN IF TG_OP = 'DELETE' THEN RETURN OLD; ELSE RETURN NEW; END IF; 
    END IF;


  IF TG_OP = 'INSERT' THEN
    v_C_INVOICE_ID:=new.C_INVOICE_ID;
