      n.Node_ID
    INTO v_Tree_ID,
      v_Parent_ID
    FROM AD_ClientInfo c,
      AD_TreeNode n
    WHERE c.AD_Tree_SalesRegion_ID=n.AD_Tree_ID
      AND n.Parent_ID IS NULL
