CREATE OR REPLACE FUNCTION dba_getstandard_search_text(text) RETURNS text
    LANGUAGE plpgsql
    AS $_$
/*************************************************************************
* The contents of this file are subject to the Openbravo  Public  License
* Version  1.0  (the  "License"),  being   the  Mozilla   Public  License
