        AND ISACTIVE = 'Y'
      )
      AND TO_DATE(VALIDFROM) <= TO_DATE(COALESCE(p_Date, TO_DATE(NOW())))
      AND EXISTS(SELECT 1
                 FROM M_PRODUCTPRICE
                 WHERE M_PRICELIST_VERSION_ID = M_PRICELIST_VERSION.M_PRICELIST_VERSION_ID
